package com.ankushn.demo.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.ankushn.demo.domain.Document;
import com.ankushn.demo.domain.Document_;
import com.ankushn.demo.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link Document} 
 *
 * @see GenericPrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class DocumentPrinter extends GenericPrinter<Document> {
    public DocumentPrinter() {
        super(Document.class, Document_.documentFileName);
    }

    @Override
    public String print(Document document, Locale locale) {
        if (document == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, document.getDocumentFileName());
        return ret.toString();
    }
}