package com.ankushn.demo.web.domain;

import javax.inject.Inject;
import javax.inject.Named;

import com.ankushn.demo.domain.Dbuser;
import com.ankushn.demo.web.domain.support.GenericExcelExporter;
import com.ankushn.demo.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link Dbuser} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class DbuserExcelExporter extends GenericExcelExporter<Dbuser> {
    @Inject
    protected DbuserSearchForm sf;

    public DbuserExcelExporter() {
        super("dbuser_username", "dbuser_createdBy", "dbuser_createdDate");
    }

    @Override
    protected void fillResultItem(int row, Dbuser item) {
        int col = 0;
        setValue(row, col++, item.getUsername());
        setValue(row, col++, item.getCreatedBy());
        setValue(row, col++, item.getCreatedDate());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "dbuser_username", sf.getUsernameSelector());
        setSelector(row++, 0, "dbuser_createdBy", sf.getCreatedBySelector());
        setRangeDate(row++, 0, "dbuser_createdDate", sf.getCreatedDateRange());
    }
}